mapper.select:

select m_id, m_name, m_regdate, m_level, m_status, m_birth
from
    (
        select rownum rn, m_id, m_name, m_regdate, m_level, m_status, m_birth
        from
            member
        where m_id  like 'm%' and rownum <= #{pageNum} * #{amount}
    )
where rn > (#{pageNum} - 1) * #{amount}

mapper.update:
update member
set m_house = #{m_house}, m_email = #{m_email}
where m_id = #{m_id}

mapper.select:
select *
from member
where substr(m_id,3,11) = #{phoneNumber}
  and m_name = #{name}

select m.*,s.s_email
from member m inner join social s on m.m_id = s.m_id
where s.s_email = #{s_email}

mapper.update:
update member
set m_point += #{fare}
where m_id = #{m_id}
